import React, { useState } from 'react';

function AddServiceModal({ onSubmit, onClose, wasteTypes, towns }) {
  const [selectedWasteType, setSelectedWasteType] = useState(wasteTypes[0]); // Use wasteTypes[0] as initial value
  const [selectedTown, setSelectedTown] = useState(towns[0]); // Use towns[0] as initial value


  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    if (selectedWasteType && selectedTown) {
      onSubmit({ wasteType: selectedWasteType, town: selectedTown }); // Call parent function with selected values // Call parent function with selected values
      // **Don't reset selections here**
    } else {
      alert('Please select the service information.');
    }
  };

  return (
    <div className="add-service-modal">
      <h2>Add Service</h2>
      <form onSubmit={handleSubmit}>
        <select value={selectedWasteType} onChange={(e) => setSelectedWasteType(e.target.value)}>
          {/* Set the first element of wasteTypes as the default selected option */}
          <option value={wasteTypes[0]}>{wasteTypes[0]}</option>
          {wasteTypes.slice(1).map((wasteType) => ( // Start mapping from index 1
            <option key={wasteType} value={wasteType}>
              {wasteType}
            </option>
          ))}
        </select>
        <select value={selectedTown} onChange={(e) => setSelectedTown(e.target.value)}>
          {/* Set the first element of towns as the default selected option */}
          <option value={towns[0]}>{towns[0]}</option>
          {towns.slice(1).map((town) => ( // Start mapping from index 1
            <option key={town} value={town}>
              {town}
            </option>
          ))}
        </select>
        <button type="submit">Add Service</button>
      </form>
      <button onClick={onClose}>Close</button>
    </div>
  );
}

export default AddServiceModal;
