import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Assuming you have React Router
import axios from 'axios'; // Import Axios for HTTP requests

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoginSelected, setIsLoginSelected] = useState(true); // Flag for login/register selection
  const [errorMessage, setErrorMessage] = useState(''); // Error message state
  const backport = 'http://localhost:8001';

  // Submit form handler

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission

    if (!email || !password) {
      setErrorMessage(''); // Clear any previous errors
      return; // Don't proceed if fields are empty
    }

    const url = isLoginSelected ? backport + '/api/user/login' : backport + '/api/user/register';
    const method = 'POST'; // Both login and registration use POST
    localStorage.setItem('userEmail', email); // Store email in local storage

    try {
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      if (!response.ok) {
        throw new Error(`Network response was not ok: ${response.statusText}`);
      }
      fetch('http://yourbackenddomain.com/api/objects')
      .then(response => response.json()) // Parse the response as JSON
      .then(data => {
        console.log(data); // Here you have your objects, and you can now use them in your frontend
        // For example, update the state in a React app to render the objects
      })
      .catch(error => {
        console.error('There was an error!', error);
      });
    console.log(reply);
      const data = await response.json();

      if (data.success) {
        // ... handle successful login/registration
      } else {
        setErrorMessage(data.message || (isLoginSelected ? 'Invalid email or password.' : 'Email exists. Please login or register with a different email.'));
      }
    } catch (error) {
      console.error(isLoginSelected ? 'Login error:' : 'Registration error:', error);
      setErrorMessage('An error occurred. Please try again.');
    }
  };










  return (
    <div className="login-register">
      <h2>{isLoginSelected ? 'Welcome! Please login' : 'New user? Please register'}</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">{isLoginSelected ? 'Login' : 'Register'}</button>
      </form>
      {errorMessage && <p className="error">{errorMessage}</p>}
      <button onClick={() => setIsLoginSelected(!isLoginSelected)}>
        {isLoginSelected ? 'New user? Please register' : 'Already have an account? Login'}
      </button>
    </div>
  );
}

export default Login;
