import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Assuming you have React Router // Assuming you have React Router
import { LoginContext } from './LoginContext';

function Profile() {
  const navigate = useNavigate();
  const [errorMessage, setErrorMessage] = useState(''); // Error message state
  const backport = 'http://localhost:8001';

  const [userProfile, setUserProfile] = useState({
    name: '',
    phone: '',
    address: '',
    town: '',
    province: '',
    country: '',
  });



  const { isLoggedIn, setUserType, setUserEmail, userProfile: contextUserProfile } = useContext(LoginContext);

  useEffect(() => {
    if (!isLoggedIn) {
      navigate('/user/login');
    }
  }, [isLoggedIn, navigate]); // Check login status on component mount and changes




  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission

    // Check if all fields are filled
    if (!name || !phone || !address ||  !town || !province || !country) {
      setErrorMessage('Please fill in all the information.');
      return; // Prevent further processing if not complete
    }

    // Simulate data update (replace with actual API call)
    try {
      const response = await fetch(backport + '/api/user/profile', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
         userProfile
        }),
      });
      navigate('/user');
      if (!response.ok) {
        console.error('Profile update failed:', response.statusText);
        setErrorMessage('Profile update failed. Please try again.');
        return; // Handle unsuccessful response
      }

      const data = await response.json();
      setUserProfile(data);
      console.log(data);
      // Assuming the backend sends a success message in case of success
      navigate('/user'); // Redirect to user page on success


    } catch (error) {
      console.error('Profile update error:', error);
      // Handle API call errors
    }
  };

  // **Event Handlers for Input Changes:**

  const handleNameChange = (e) => {
    setUserProfile({ ...userProfile, name: e.target.value }); // Update name in state
  };

  const handlePhoneChange = (e) => {
    setUserProfile({ ...userProfile, phone: e.target.value }); // Update phone in state
  };

  // **Event Handlers for Input Changes:**

  const handleAddressChange = (e) => {
    setUserProfile({ ...userProfile, address: e.target.value }); // Update name in state
  };

  const handleTownChange = (e) => {
    setUserProfile({ ...userProfile, town: e.target.value }); // Update phone in state
  };
// **Event Handlers for Input Changes:**

const handleProvinceChange = (e) => {
  setUserProfile({ ...userProfile, province: e.target.value }); // Update name in state
};

const handleCountryChange = (e) => {
  setUserProfile({ ...userProfile, country: e.target.value }); // Update phone in state
};

const handleLogout = () => {
  // Handle logout logic (clear login context, redirect)
  setUserType('');
  setUserEmail('');
  setUserProfile(''); // Clear user profile if needed
  navigate('/'); // Redirect to home on logout
};

  const { name = '', phone = '', address = '', town = '', province = '', country = '' } = userProfile;
return (
  <div className="profile">
    <nav className="navigation-bar">
        {/* Assuming you have basic CSS styling for navigation-bar */}
        <h1>Recycling Made Simple</h1>
        <div className="navigation-right">
        {isLoggedIn && (
          <>
          <p>Logged in as: {userEmail}</p>
          <button onClick={handleLogout}>Logout</button>
        </>
      )}
      {!isLoggedIn && <Link to="/user/login">Login</Link>}
    </div>
  </nav>

    <h1>Welcome</h1>
    <h1>Your Profile</h1>
    <form onSubmit={handleSubmit}>
      <div className="input-container">
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" value={name} onChange={handleNameChange} required />
      </div>

          <div className="input-container">
            <label htmlFor="phone">Phone:</label>
            <input type="tel" id="phone" value={phone} onChange={handlePhoneChange} required />
          </div>
          <div className="input-container">
            <label htmlFor="address">Address:</label>
            <input type="text" id="address" value={address} onChange={handleAddressChange} required />
          </div>
          <div className="input-container">
  <label htmlFor="town">Town:</label>
  <select id="town" value={town} onChange={handleTownChange} required>
    <option value="">-- Select Town --</option>
    <option value="Surrey">Surrey</option>
    <option value="Burnaby">Burnaby</option>
    <option value="Vancouver">Vancouver</option>
    <option value="Richmond">Richmond</option>
  </select>
</div>
          <div className="input-container">
            <label htmlFor="province">Province:</label>
            <input type="text" id="province" value={province} onChange={handleProvinceChange} required />
          </div>
          <div className="input-container">
            <label htmlFor="country">Country:</label>
            <input type="text" id="country" value={country} onChange={handleCountryChange} required />
          </div>
          <button type="submit">Submit</button>
          {errorMessage && <p className="error">{errorMessage}</p>}
        </form>

    ) : (
      <p>You are not logged in. Please <Link to="/user/login">login</Link> to access your profile.</p>
    )}
    </div>
);
  }

export default Profile;
