import { useNavigate } from 'react-router-dom';
import React, { useState, useRef } from 'react';
import Login from './Login';
import Video from './Video'; // Assuming Video.js is in a separate file
import './App.css';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function App() {
  const avatar = "/recycle.png";
  const [isPlaying, setIsPlaying] = useState(false); // Video playing state
  const logoRef = useRef(null); // Ref to hold logo element
  const navigate = useNavigate(); // Hook for navigation

  const handleLogin = () => {
    navigate('/user/login'); // Navigate to /login route on button click
  };

  const handleProvider = () => {
    navigate('/provider/login'); // JSX inside functions is not allowed. Render Register conditionally.
  };

  const handlePlay = () => {
    setIsPlaying(true);
    logoRef.current.style.display = 'none'; // Hide logo on play
    //logoRef.current.parentElement.style.position = 'relative'; // Set relative positioning on parent
  };

  return (
    <div className="App">
      <header className="App-header d-flex flex-column justify-content-center align-items-center bg-success text-white">
        <div className="profile rounded-circle overflow-hidden d-flex justify-content-center align-items-center border border-2 border-white" ref={logoRef}>
          {isPlaying && <Video />} {/* Conditionally render Video within logo's parent */}
          <img src={avatar} alt="logo" className="App-logo" /> {/* Hide logo initially */}
        </div>
        <h1>Recycling Made Easy</h1>
        <p className="lead">Recycling doesn't have to be complicated! <br /> Our platform helps you understand what can be recycled in your area, matches you with service providers and simplifies the process.</p>
        <div className="buttons d-flex justify-content-center mt-4">
          <Button variant="outline-light" className="me-3" onClick={handleLogin}>
            User Login
          </Button>
          <Button variant="outline-light" className="me-3" onClick={handleProvider}>
            Recycling Provider Login
          </Button>
          <Button variant="light" onClick={handlePlay}>
            Play Video
          </Button>
        </div>
      </header>
    </div>
  );
}

export default App;
