import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate hook

const Login = ({ onClose }) => {
  const navigate = useNavigate(); // Get the navigation function
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleClose = () => {
    onClose && onClose(); // Call the provided onClose function if available
  };

  const handleLogin = (e) => {
    e.preventDefault(); // Prevent default form submission behavior

    // Replace with your actual login logic and authentication
    console.log('Login attempt with username:', username, 'and password:', password);

    // After successful login (replace with actual logic)
    navigate('/login'); // Navigate to /login page (for illustration)
  };

  return (
    <div className="login-signup">
      <h2>Welcome!</h2>
      <form onSubmit={handleLogin}>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleClose}>Close</button>
        <button type="submit">Login</button>
      </form>
      <button>Login as Service Provider</button>
      <p>Don't have an account? Sign up here!</p>
    </div>
  );
};

export default Login;
