import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Assuming you have React Router

function LoginRegister() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoginSelected, setIsLoginSelected] = useState(true); // Flag for login/register selection
  const [errorMessage, setErrorMessage] = useState(''); // Error message state

  // Simulate login/register logic (replace with actual API calls)
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission

    if (!email || !password) {
      setErrorMessage('Please fill in all fields.');
      return;
    }

    const url = isLoginSelected ? '/api/login' : '/api/register';
    const method = isLoginSelected ? 'POST' : 'POST';
    const body = JSON.stringify({ email, password });

    try {
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body,
      });
      const data = await response.json();

      if (data.success) {
        if (isLoginSelected) {
          setIsLoginSelected(false); // Switch to profile section after successful login
          navigate('/profile'); // Assuming '/profile' route for user profile
          // Check profile completeness and handle profile information (replace with your logic)
        } else {
          // Registration successful, even if email doesn't exist yet (handled on backend)
          setErrorMessage('Registration successful! Please check your inbox for verification (if applicable).'); // More informative message
          setEmail(''); // Clear input fields after registration
          setPassword('');
        }
      } else {
        // Handle specific error messages from backend
        setErrorMessage(data.message || (isLoginSelected ? 'Login failed.' : 'Registration failed.')); // Use backend error message if available
      }
    } catch (error) {
      console.error(isLoginSelected ? 'Login error:' : 'Registration error:', error);
      setErrorMessage(isLoginSelected ? 'Login error. Please try again.' : 'Registration error. Please try again.'); // Generic error for unexpected situations
    }
  };

  return (
    <div className="login-register">
      <h2>{isLoginSelected ? 'Welcome! Please login' : 'New user? Please register'}</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" value={email} onChange={(e) => setEmail(e.triggerValidation)} required /> {/* Consider adding validation logic here */}
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">{isLoginSelected ? 'Login' : 'Register'}</button>
      </form>
      {errorMessage && <p className="error">{errorMessage}</p>}
      <button onClick={() => setIsLoginSelected(!isLoginSelected)}>
        {isLoginSelected ? 'New user? Please register' : 'Already have an account? Login'}
      </button>
    </div>
  );
}

export default LoginRegister;
