import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { LoginContext } from './LoginContext'; // Assuming LoginContext.js is in the same directory
import Navigation from './NavigationBar'; // Import Navigation component
import axios from 'axios'; // Import Axios for HTTP requests

function ServiceOfferPage() {
  const navigate = useNavigate();
  const { isLoggedIn, userEmail, userType, userProfile } = useContext(LoginContext);
  const [services, setServices] = useState([]);
  const [wasteTypes] = useState(['paper', 'steel', 'aluminum', 'laptop', 'computer', 'cell phone', 'screen', 'bed', 'table', 'chair']);
  const [towns] = useState(['Surrey', 'Burnaby', 'Vancouver', 'Richmond']);
  const [addedServices, setAddedServices] = useState([]); // Array to track added services (with location_id)
  const [deletedServices, setDeletedServices] = useState([]); // Array to track deleted services (with location_id)
  const backendUrl = 'http://localhost:8001'; // Replace with your backend URL
  const [addServiceModalOpen, setAddServiceModalOpen] = useState(false);
  const [selectedWasteType, setSelectedWasteType] = useState(''); // State for selected waste type
  const [selectedLocationId, setSelectedLocationId] = useState(''); // State for selected location ID (location_id from API)

  // Function to convert location_id to town name (replace with your implementation)
  const idToTown = (locationId) => {
    // Implement your logic to convert location_id to town name
    // Replace with the actual conversion logic (e.g., using a map or API call)
    return `Town ${locationId}`; // Placeholder for conversion
  };

  const handleOpenAddServiceModal = () => setAddServiceModalOpen(true);
  const handleCloseAddServiceModal = () => setAddServiceModalOpen(false);

  const handleWasteTypeChange = (event) => {
    setSelectedWasteType(event.target.value);
  };

  const handleLocationChange = (event) => {
    setSelectedLocationId(event.target.value);
  };

  const handleDeleteService = (service) => {
    if (window.confirm('Are you sure you want to delete this service option?')) {
      setDeletedServices([...deletedServices, service]); // Add to deleted services
      setServices(services.filter((item) => item !== service)); // Remove from displayed list immediately
    }
  };

  const handleAddServiceSubmit = () => {
    if (!selectedWasteType || !selectedLocationId) {
      alert('Please select both waste type and location from the dropdowns.');
      return;
    }

    const newService = {
      waste_type: selectedWasteType,
      location_id: selectedLocationId,
    };

    const isDuplicate = services.some(
      (service) => service.waste_type.toLowerCase() === newService.waste_type.toLowerCase()
    );
    if (isDuplicate) {
      alert('This service option already exists.');
    } else {
      setAddedServices([...addedServices, newService]); // Add to added services
      setServices([...services, { ...newService, town: idToTown(newService.location_id) }]); // Add to displayed list with town name
      setSelectedWasteType(''); // Clear selected values after submission
      setSelectedLocationId('');
    }
  };

  const handleConfirmChanges = async () => {
    if (addedServices.length === 0 && deletedServices.length === 0) {
      alert('No changes to confirm.');
      return;
    }

    const formattedAddedServices = addedServices.map((service) => ({
      waste_type: service.waste_type,
      location_id: service.location_id,
    }));
    const formattedDeletedServices = deletedServices.map((service) => ({
      waste_type: service.waste_type,
      location_id: service.location_id,
    }));

    // Replace with your actual API calls for deletions and additions
    const deletePromise = axios.post(`${backendUrl}/api/provider/service/delete`, {
      id: userProfile.id,
      service: formattedDeletedServices,
    });

    const addPromise = axios.post(`${backendUrl}/api/provider/service/add`, {
      id: userProfile.id,
      service: formattedAddedServices,
    });

    try {
      const [deleteResult] = await Promise.all([deletePromise, addPromise]);
      if (deleteResult.data.ok && addPromise.data.ok) {
        setAddedServices([]);
        setDeletedServices([]);
        // Update displayed services if needed (considering backend updates)
        const updatedServices = await axios.post(`${backendUrl}/api/provider/service`, { id: userProfile.id });
        setServices(updatedServices.data.map((service) => ({ ...service, town: idToTown(service.location_id) })));
        alert('Changes confirmed successfully!');
      } else {
        console.error('Error confirming changes:', deleteResult.data, addPromise.data);
        alert('An error occurred while saving changes. Please try again.');
      }
    } catch (error) {
      console.error('Error during API calls:', error);
      alert('An error occurred while communicating with the server. Please try again later.');
    }
  };

  // Fetch services on initial render and whenever login state changes
  useEffect(() => {
    const fetchServices = async () => {
      if (isLoggedIn) {
        try {
          const url = `${backendUrl}/api/provider/service`;
          const response = await axios.post(url, { id: userProfile.id });
          const record = response.data;

          if (Array.isArray(record)) {
            const servicesWithTownNames = record.map((service) => ({
              ...service,
              town: idToTown(service.location_id),
            }));
            setServices(servicesWithTownNames);
          } else {
            // Handle non-array response (e.g., log a message or display an error)
            console.log('Received unexpected response format:', response.data);
          }
        } catch (error) {
          console.error('Error fetching services:', error);
          // Handle errors appropriately (e.g., display an error message)
        }
      }
    };
    fetchServices();
  }, [isLoggedIn, userProfile]);

  // ... (JSX for rendering the page content)

  return (
    <div>
      <h2>My Service Offerings</h2>

      {/* Conditionally render content based on login status */}
      {isLoggedIn ? (
        <>
          <ul>
            {services.map((service) => (
              <li key={`${service.waste_type}-${service.town}`}>
                {service.waste_type} - {service.town} (
                <button onClick={() => handleDeleteService(service)}>Delete</button>
                )
              </li>
            ))}
          </ul>
          <button onClick={handleOpenAddServiceModal}>Add Service</button>

          {addServiceModalOpen && (
            <AddServiceModal
              wasteTypes={wasteTypes} // Assuming you have a wasteTypes array
              towns={towns} // Assuming you have a towns array
              onSubmit={handleAddServiceSubmit}
              onClose={handleCloseAddServiceModal}
              selectedWasteType={selectedWasteType} // Pass the selected waste type as a prop
              onWasteTypeChange={handleWasteTypeChange} // Pass the waste type change handler
              selectedLocationId={selectedLocationId} // Pass the selected location ID as a prop
              onLocationChange={handleLocationChange} // Pass the location change handler
            />
          )}

          <button onClick={handleConfirmChanges}>Confirm Changes</button>
          <div id="confirmation-message"></div>
        </>
      ) : (
        <p>
          Please login to view and manage your service offerings.
          <br />
          <button onClick={() => navigate('/provider-login')}>Login</button>
        </p>
      )}
    </div>
  );
}

export default ServiceOfferPage;
