import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import LoginForm from './LoginForm'; // Import LoginForm component

function Login() {
  const navigate = useNavigate(); // Hook for navigation
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    // Implement your actual login logic here (e.g., API call, handle success/failure)
    console.log('Login attempt received:', username, password);

    // Handle successful login (e.g., navigate to a different page using useNavigate)
    navigate('/dashboard'); // Replace with your desired route after login
  };

  return (
    <div className="login-signup">
       document.body.innerHTML = '';
      <h2>Welcome! Please Login</h2>
      <LoginForm
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
        onSubmit={handleLogin} // Pass the handleLogin function as a prop
      />
    </div>
  );
}

export default Login;
