import React, { useState, useRef } from 'react';
import Login from './Login'; // Import your Login component
import Register from './Register'; // Import your Register component (optional)
import Video from './Video'; // Import your Video component (optional)
import './App.css';
import { Link, Routes, Route, BrowserRouter } from 'react-router-dom'; // Import Link and Routes components

function App() {
  const avatar = "/recycle.png";
  const [isPlaying, setIsPlaying] = useState(false); // Video playing state
  const [showLogin, setShowLogin] = useState(false); // State to control login visibility

  const logoRef = useRef(null); // Ref to hold logo element

  const handleLogin = (username, password) => {
    // Implement your actual login logic here (e.g., API call, handle success/failure)
    console.log('Login attempt received from Login component:', username, password);
    // Handle successful login (e.g., navigate to a different page)
  };

  const handleRegistration = () => {
    // Handle registration logic here (consider using a separate Register component)
  };

  const handlePlay = () => {
    setIsPlaying(true);
    logoRef.current.style.display = 'none'; // Hide logo on play
    //logoRef.current.parentElement.style.position = 'relative'; // Set relative positioning on parent
  };

  return (
    <BrowserRouter> {/* Single top-level Router component */}
      <div className="App">
        <header className="App-header">
          <div className="profile" ref={logoRef}>
            {isPlaying && <Video />}  {/* Conditionally render Video within logo's parent */}
            <img src={avatar} alt="logo" className="App-logo" /> {/* Hide logo initially */}
          </div>
          <h1>Recycling Made Easy</h1>
          <p>
            Recycling doesn't have to be complicated!<br />
            Our platform helps you understand what can be recycled in your area, <br />
            matches with service providers and simplifies the process.
          </p>
          <div className="buttons">
            <Link to="/login" className="button">Login</Link>
            {showLogin && <Login onClose={() => setShowLogin(false)} onSubmit={handleLogin} />} {/* Conditionally render Login with onClose and onSubmit props */}
            <button className="button" onClick={handleRegistration}>
              Register
            </button>
            <button className="button" onClick={handlePlay}>
              Play Video
            </button>
          </div>
        </header>
        <Routes>  {/* Define routes here */}
          <Route path="/" element={<div>Home Content</div>} />  {/* Placeholder content for home route */}
          <Route path="/login" element={<Login onSubmit={handleLogin} />} />  {/* Login route renders Login component */}
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
